import cloneRamda from 'ramda/src/clone';
import cloneLodash from 'lodash/cloneDeep';
import clone from '../clone';

const testObj = {
    a: 1,
    b: {
        a: [1, 2, 3, 4, 5],
        b: {
            a: 'test123',
            b: /aworw/g,
            c: new Date(),
            d: {
                a: 1898983759325,
                b: {},
                c: null
            }
        },
        c: null
    },
    c: 3,
    d: undefined
};

export default {
    ramda: () => {
        cloneRamda(testObj);
    },
    lodash: () => {
        cloneLodash(testObj);
    },
    utils: () => {
        clone(testObj);
    }
};

import clone from '../clone';

describe('utils/clone', () => {
    it('should return copy of all nested objects', () => {
        const obj = { a: { b: { c: 1 } }, d: new Date(), t: 'test', arr: [1, 2, { b: 8 }], b: true };
        const result = clone(obj);

        expect(result).not.toBe(obj);
        expect(result).toEqual(obj);
        expect(result.a.b).not.toBe(obj.a.b);
        expect(result.a.b).toEqual(obj.a.b);
        expect(result.d).not.toBe(obj.d);
        expect(result.d).toEqual(obj.d);
        expect(result.arr).not.toBe(obj.arr);
        expect(result.arr).toEqual(obj.arr);
        expect(result.arr[2]).not.toBe(obj.arr[2]);
        expect(result.arr[2]).toEqual(obj.arr[2]);
    });
});

import defaultTo from '../defaultTo';

describe('utils/defaultTo', () => {
    it('test defaultTo', () => {
        const d = defaultTo(42);

        expect(d(3)).toBe(3);
        expect(d(null)).toBe(42);
        expect(d(0 / 0)).toBe(42);
        expect(defaultTo(38, 22)).toBe(22);
        expect(d('test')).toBe('test');
    });
});

import isTrue from '../isTrue';

describe('isTrue', () => {
    it('works correctly with boolean', () => {
        expect(isTrue(true)).toBeTruthy();
        expect(isTrue(false)).toBeFalsy();
    });
    it('works correctly with string', () => {
        expect(isTrue('true')).toBeTruthy();
        expect(isTrue('false')).toBeFalsy();
    });
});

import negate from '../negate';

describe('utils/negate', () => {
    it('should negate number', () => {
        expect(negate(42)).toBe(-42);
        expect(negate(-42)).toBe(42);
        expect(negate(0)).toBe(-0);
    });
});

import not from '../not';

describe('utils/not', () => {
    it('returns the logical inverse of passed arg', () => {
        expect(not(1)).toBe(false);
        expect(not(0)).toBe(true);
        expect(not(false)).toBe(true);
        expect(not('2323')).toBe(false);
        expect(not({})).toBe(false);
    });
});

import sanitize from '../sanitize';

describe('utils/sanitize', () => {
    it('test', () => {
        expect(sanitize('em', 'test123')).toBe('test123');
        expect(sanitize('em', '<em>abc</em>')).toBe('abc');
        expect(sanitize('unknown', '<em>123</em>')).toBe('<em>123</em>');
    });
});

import type from '../type';

describe('utils/type', () => {
    it('should return type', () => {
        expect(type(undefined)).toBe('Undefined');
        expect(type(null)).toBe('Null');
        expect(type(-3)).toBe('Number');
        expect(type('num')).toBe('String');
        expect(type(new Date())).toBe('Date');
        expect(type({ a: 1 })).toBe('Object');
        expect(type([1, 2, 3])).toBe('Array');
        expect(type(/123/g)).toBe('RegExp');
        expect(type(false)).toBe('Boolean');
    });
});

import uniqueId from '../uniqueId';

describe('utils/uniqueId', () => {
    it('should return different ids', () => {
        const a = uniqueId();
        const b = uniqueId();

        expect(a).toBeDefined();
        expect(b).toBeDefined();
        expect(a).not.toEqual(b);
    });
});

import filterRamda from 'ramda/src/filter';
import filterLodash from 'lodash/filter';
import lazy from 'lazy.js';
import _ from 'underscore';
import filter from '../filter';
import range from '../range';

const fn = x => x % 2;
const array = range(101);

export default {
    ramda: () => {
        filterRamda(fn, array);
    },
    lodash: () => {
        filterLodash(array, fn);
    },
    underscore: () => {
        _.filter(array, fn);
    },
    lazyjs: () => {
        lazy(array).filter(fn).toArray();
    },
    utils: () => {
        filter(fn, array);
    }
};

import adjust from '../adjust';

describe('utils/array/adjust', () => {
    const add = x => x + 1;

    it('applies the given function to the value at the given index of the supplied array', () => {
        expect(adjust(x => x + 10, 1, [1, 2, 3])).toEqual([1, 12, 3]);
        expect(adjust(add, 2, [0, 1, 2, 3])).toEqual([0, 1, 3, 3]);
    });

    it('offsets negative indexes from the end of the array', () => {
        expect(adjust(add, -3, [0, 1, 2, 3])).toEqual([0, 2, 2, 3]);
    });

    it('returns the original array if the supplied index is out of bounds', () => {
        const list = [0, 1, 2, 3];

        expect(adjust(add, 4, list)).toEqual(list);
        expect(adjust(add, -5, list)).toEqual(list);
    });

    it('does not mutate the original array', () => {
        const list = [0, 1, 2, 3];

        expect(adjust(add, 2, list)).toEqual([0, 1, 3, 3]);
        expect(list).toEqual([0, 1, 2, 3]);
    });

    it('curries the arguments', () => {
        expect(adjust(add)(2)([0, 1, 2, 3])).toEqual([0, 1, 3, 3]);
    });
});

import all from '../all';

describe('utils/array/all', () => {
    it('test', () => {
        expect(all(x => x > 1, [1, 2, 3])).toBe(false);
        expect(all(x => x < 5, [1, 2, 3, 4])).toBe(true);
        expect(all(x => true, [])).toBe(true);
    });
});

import any from '../any';

describe('utils/array/any', () => {
    it('test', () => {
        expect(any(x => x > 3, [1, 2, 3])).toBe(false);
        expect(any(x => x > 3, [1, 2, 3, 4])).toBe(true);
        expect(any(x => true, [])).toBe(false);
    });
});

import append from '../append';

describe('utils/array/append', () => {
    it('should add element to head', () => {
        expect(append('tests', ['write', 'more'])).toEqual(['write', 'more', 'tests']);
        expect(append('tests', [])).toEqual(['tests']);
        expect(append(['tests'], ['write', 'more'])).toEqual(['write', 'more', ['tests']]);
    });
    it('adds the element to the end of the list', () => {
        expect(append('z', ['x', 'y'])).toEqual(['x', 'y', 'z']);
        expect(append(['a', 'z'], ['x', 'y'])).toEqual(['x', 'y', ['a', 'z']]);
    });

    it('works on empty list', () => {
        expect(append(1, [])).toEqual([1]);
    });

    it('is curried', () => {
        expect(typeof append(4)).toEqual('function');
        expect(append(1)([4, 3, 2])).toEqual([4, 3, 2, 1]);
    });
});

import concat from '../concat';

describe('utils/array/concat', () => {
    it('should concatenate two arrays', () => {
        expect(concat([1, 2], [3, 4, 5])).toEqual([1, 2, 3, 4, 5]);
        expect(concat([], [1])).toEqual([1]);
    });

    it('should concatenate two strings', () => {
        expect(concat('123', '456')).toBe('123456');
        expect(concat('', '34')).toBe('34');
    });
});

import difference from '../difference';

describe('utils/array/difference', () => {
    it('test', () => {
        expect(difference([1, 2, 3, 4], [3, 5, 6, 7])).toEqual([1, 2, 4]);
        expect(difference([1, 1, 2], [2])).toEqual([1, 1]);
        expect(difference([1, 1, 1], [1, 2, 3, 4])).toEqual([]);
    });
});

import drop from '../drop';

describe('utils/array/drop', () => {
    it('should drop', () => {
        expect(drop(1, ['foo', 'bar', 'baz'])).toEqual(['bar', 'baz']);
        expect(drop(2, ['foo', 'bar', 'baz'])).toEqual(['baz']);
        expect(drop(3, ['foo', 'bar', 'baz'])).toEqual([]);
    });
    it('skips the first `n` elements from a list, returning the remainder', () => {
        expect(drop(3, ['a', 'b', 'c', 'd', 'e', 'f', 'g'])).toEqual(['d', 'e', 'f', 'g']);
    });

    it('returns an empty array if `n` is too large', () => {
        expect(drop(20, ['a', 'b', 'c', 'd', 'e', 'f', 'g'])).toEqual([]);
    });

    it('returns an equivalent list if `n` is <= 0', () => {
        expect(drop(0, [1, 2, 3])).toEqual([1, 2, 3]);
        expect(drop(-1, [1, 2, 3])).toEqual([1, 2, 3]);
        expect(drop(-Infinity, [1, 2, 3])).toEqual([1, 2, 3]);
    });

    it('never returns the input array', () => {
        const xs = [1, 2, 3];

        expect(drop(0, xs)).toEqual(xs);
        expect(drop(-1, xs)).toEqual(xs);
    });
});

import dropLast from '../dropLast';

describe('utils/array/dropLast', () => {
    it('should dropLast', () => {
        expect(dropLast(1, ['foo', 'bar', 'baz'])).toEqual(['foo', 'bar']);
        expect(dropLast(2, ['foo', 'bar', 'baz'])).toEqual(['foo']);
        expect(dropLast(3, ['foo', 'bar', 'baz'])).toEqual([]);
        expect(dropLast(4, ['foo', 'bar', 'baz'])).toEqual([]);
        expect(dropLast(3, 'ramda')).toEqual('ra');
    });

    it('skips the last `n` elements from a list, returning the remainder', () => {
        expect(dropLast(3, ['a', 'b', 'c', 'd', 'e', 'f', 'g'])).toEqual(['a', 'b', 'c', 'd']);
    });

    it('returns an empty array if `n` is too large', () => {
        expect(dropLast(20, ['a', 'b', 'c', 'd', 'e', 'f', 'g'])).toEqual([]);
    });

    it('returns an equivalent list if `n` is <= 0', () => {
        expect(dropLast(0, [1, 2, 3])).toEqual([1, 2, 3]);
        expect(dropLast(-1, [1, 2, 3])).toEqual([1, 2, 3]);
        expect(dropLast(-Infinity, [1, 2, 3])).toEqual([1, 2, 3]);
    });

    it('never returns the input array', () => {
        const xs = [1, 2, 3];

        expect(dropLast(0, xs)).not.toBe(xs);
        expect(dropLast(-1, xs)).not.toBe(xs);
    });

    it('can operate on strings', () => {
        expect(dropLast(3, 'Ramda')).toEqual('Ra');
    });

    it('is curried', () => {
        const dropLast2 = dropLast(2);

        expect(dropLast2(['a', 'b', 'c', 'd', 'e'])).toEqual(['a', 'b', 'c']);
        expect(dropLast2(['x', 'y', 'z'])).toEqual(['x']);
    });
});

import dropWhile from '../dropWhile';

describe('utils/array/dropWhile', () => {
    it('test', () => {
        const f = jest.fn(x => x < 3);

        expect(dropWhile(f, [1, 2, 3, 4, 5])).toEqual([3, 4, 5]);
        expect(f).toBeCalledWith(1);
        expect(f).toBeCalledWith(2);
        expect(f).toBeCalledWith(3);
        expect(f).toHaveBeenCalledTimes(3);

        expect(dropWhile(f, [])).toEqual([]);
    });
});

import each from '../each';

describe('utils/array/each', () => {
    it('should traverse all elements', () => {
        const f = jest.fn();
        const arr = [1, 2, 3];

        expect(each(f, arr)).toBeUndefined();
        expect(f).toBeCalledWith(1, 0, arr);
        expect(f).toBeCalledWith(2, 1, arr);
