import flatten from '../flatten';

describe('utils/array/flatten', () => {
    it('should return flatten array', () => {
        expect(flatten([1, [2, 3], [4, [5, [6], 7], 8]])).toEqual([1, 2, 3, 4, 5, 6, 7, 8]);
