import last from '../last';

describe('utils/array/last', () => {
    it('should return last element in array', () => {
        expect(last([1, 2, 3, 4, 5])).toBe(5);
        expect(last([1, 2, 3])).toBe(3);
        expect(last([])).toBeUndefined();
    });
});

import length from '../length';

describe('utils/array/length', () => {
    it('should return length of array', () => {
        expect(length([])).toBe(0);
        expect(length([1])).toBe(1);
        expect(length([1, 2, 3, 4, 5])).toBe(5);
    });
});

import map from '../map';

describe('utils/array/map', () => {
    it('should return new mapped array', () => {
        const arr = [1, 2, 3, 4, 6];
        const f = jest.fn(x => x * 2);

        expect(map(f)(arr)).toEqual([2, 4, 6, 8, 12]);
        expect(f.mock.calls.length).toBe(5);
        expect(f).toBeCalledWith(1, 0, arr);
        expect(f).toBeCalledWith(6, 4, arr);
    });
});

import maxBy from '../maxBy';

describe('utils/array/maxBy', () => {
    it('test', () => {
        const f = jest.fn(x => -x);

        expect(maxBy(f, [1, 2, 3, 4, 5])).toBe(1);
        expect(f).toHaveBeenCalledTimes(5);
    });
});

import nth from '../nth';

describe('utils/array/nth', () => {
    it('should return value at given index', () => {
        const arr = [1, 2, 3, { a: 1 }, [3, 4]];

        expect(nth(0, arr)).toBe(1);
        expect(nth(2)(arr)).toBe(3);
        expect(nth(4, arr)).toEqual([3, 4]);
        expect(nth(-1, arr)).toEqual([3, 4]);
        expect(nth(-5, arr)).toEqual(1);
    });
});

import partition from '../partition';

describe('utils/array/partition', () => {
    it('should return two arrays', () => {
        expect(partition(x => x < 3, [1, 2, 3, 4, 5])).toEqual([[1, 2], [3, 4, 5]]);
        expect(partition(x => x < 5, [1, 2, 3])).toEqual([[1, 2, 3], []]);
        expect(partition(x => x % 2, [1, 2, 3, 4, 5])).toEqual([[1, 3, 5], [2, 4]]);
        expect(partition(x => x > 5, [1, 2, 3])).toEqual([[], [1, 2, 3]]);
    });
});

import pluck from '../pluck';

describe('pluck', () => {
    it('returns a new list by plucking the same named property off all objects in the list supplied', () => {
        expect(pluck('a', [{ a: 1 }, { a: 2 }])).toEqual([1, 2]);
        expect(pluck(0, [[1, 2], [3, 4]])).toEqual([1, 3]);
    });
});

import range from '../range';

describe('utils/array/range', () => {
    it('should return array', () => {
        expect(range(4)).toEqual([0, 1, 2, 3]);
        expect(range(1, 4)).toEqual([1, 2, 3]);
        expect(range(-4)).toEqual([0, -1, -2, -3]);
        expect(range(0, 20, 5)).toEqual([0, 5, 10, 15]);
        expect(range(0, -4, -2)).toEqual([0, -2]);
        expect(range(0)).toEqual([]);
    });
});

import reduce from '../reduce';

describe('utils/array/reduce', () => {
    it('should reduce array', () => {
        expect(reduce((x, y) => 10 * x + y, 0, [1, 2, 3, 4, 5])).toBe(12345);
    });
});

import T from '../../function/T';
import reduceWhile from '../reduceWhile';

describe('utils/array/reduceWhile', () => {
    it('should reduce normally while predicate returns truthy values', () => {
        expect(reduceWhile(T, (x, y) => 10 * x + y, 0, [1, 2, 3, 4, 5])).toBe(12345);
    });
    it('should stop reducing and return current accumulated value when predicate returns false', () => {
        const pred = jest.fn((acc, next) => next < 4);
        const iter = jest.fn((x, y) => 10 * x + y);

        const result = reduceWhile(pred, iter, 0, [1, 2, 3, 4, 5]);

        expect(result).toBe(123);
        expect(iter).toHaveBeenCalledTimes(3);
        expect(pred).toHaveBeenCalledTimes(4);
    });
});

import reject from '../reject';

describe('utils/array/reject', () => {
    it('should reject arrays', () => {
        expect(reject(() => true, [])).toEqual([]);
        expect(reject(x => x > 2, [1, 2, 3, 4, 5])).toEqual([1, 2]);
        expect(reject(() => true, [1, 2, 3, 4, 5])).toEqual([]);
        expect(reject(() => false, [1, 2, 3, 4, 5])).toEqual([1, 2, 3, 4, 5]);
    });
});

import remove from '../remove';

describe('utils/array/remove', () => {
    it('should remove third and fourth element', () => {
        expect(remove(2, 3, [1, 2, 3, 4, 5, 6, 7, 8])).toEqual([1, 2, 6, 7, 8]);
    });
    it('splices out a sub-list of the given list', () => {
        const list = ['a', 'b', 'c', 'd', 'e', 'f', 'g', 'h', 'i', 'j'];

        expect(remove(2, 5, list)).toEqual(['a', 'b', 'h', 'i', 'j']);
    });

    it('returns the appropriate sublist when start == 0', () => {
        const list = ['a', 'b', 'c', 'd', 'e', 'f', 'g', 'h', 'i', 'j'];

        expect(remove(0, 5, list)).toEqual(['f', 'g', 'h', 'i', 'j']);
        expect(remove(0, 1, list)).toEqual(['b', 'c', 'd', 'e', 'f', 'g', 'h', 'i', 'j']);
        expect(remove(0, list.length, list)).toEqual([]);
    });

    it('removes the end of the list if the count is too large', () => {
        const list = ['a', 'b', 'c', 'd', 'e', 'f', 'g', 'h', 'i', 'j'];

        expect(remove(2, 20, list)).toEqual(['a', 'b']);
    });

    it('retains the entire list if the start is too large', () => {
        const list = ['a', 'b', 'c', 'd', 'e', 'f', 'g', 'h', 'i', 'j'];

        expect(remove(13, 3, list)).toEqual(['a', 'b', 'c', 'd', 'e', 'f', 'g', 'h', 'i', 'j']);
    });

    it('is curried', () => {
        const list = ['a', 'b', 'c', 'd', 'e', 'f', 'g', 'h', 'i', 'j'];

