import concat from './concat';
import curryN from '../function/curryN';

/**
 * Returns a new list containing the contents of the given list, followed by
 * the given element.
 *
 * @param {*} el The element to add to the end of the new list.
 * @param {Array} list The list of elements to add a new item to.
 *        list.
 * @return {Array} A new list containing the elements of the old list followed by `el`.
 * @example
 *
 *      append('tests', ['write', 'more']); //=> ['write', 'more', 'tests']
 *      append('tests', []); //=> ['tests']
 *      append(['tests'], ['write', 'more']); //=> ['write', 'more', ['tests']]
 */

export default curryN(2, (el, list) => (
    concat(list, [el])
));

import curryN from '../function/curryN';
import isArray from '../is/array';

/**
 * Returns the result of concatenating the given arrays or strings.
 *
 * @param {Array|String} a The first list
 * @param {Array|String} b The second list
 * @return {Array|String} A list consisting of the elements of `firstList` followed by the elements of
 * `secondList`.
 *
 * @example
 *
 *      concat('ABC', 'DEF'); // 'ABCDEF'
 *      concat([4, 5, 6], [1, 2, 3]); //=> [4, 5, 6, 1, 2, 3]
 *      concat([], []); //=> []
 */
export default curryN(2, (a = [], b = []) => {
    if (isArray(a)) {
        return a.concat(b);
    }

    return a + b;
});

import curryN from '../function/curryN';

/**
 * Returns the array of all elements in the first array not
 * contained in the second array.
 *
 * @param {Array} a The first array.
 * @param {Array} b The second array.
 * @return {Array} The elements in `a` that are not in `b`.
 * @example
 *
 *      difference([1,2,3,4], [7,6,5,4,3]); //=> [1,2]
 *      difference([7,6,5,4,3], [1,2,3,4]); //=> [7,6,5]
 */
export default curryN(2, (a = [], b = []) => {
    const result = [];

    for (let i = 0; i < a.length; i++) {
        if (b.indexOf(a[i]) < 0) {
            result.push(a[i]);
        }
    }

    return result;
});

import slice from './slice';
import curryN from '../function/curryN';
/**
 * Returns all but the first `n` elements of the given list, string.
 *
 * @param {Number} n
 * @param {*} xs
 * @return {*} A copy of list without the first `n` elements
 * @example
 *
 *      drop(1, ['foo', 'bar', 'baz']); //=> ['bar', 'baz']
 *      drop(2, ['foo', 'bar', 'baz']); //=> ['baz']
 *      drop(3, ['foo', 'bar', 'baz']); //=> []
 *      drop(4, ['foo', 'bar', 'baz']); //=> []
 */

export default curryN(2, (n, xs) => (
    slice(Math.max(0, n), Infinity, xs)
));

import curryN from '../function/curryN';

/**
 * Returns a new list excluding the leading elements of a given list which
 * satisfy the supplied predicate function. It passes each value to the supplied
 * predicate function, skipping elements while the predicate function returns
 * `true`.
 *
 * @param {Function} fn The function called per iteration.
 * @param {Array} arr The collection to iterate over.
 * @return {Array} A new array.
 * @example
 *
 *      var lteTwo = x => x <= 2;
 *
 *      dropWhile(lteTwo, [1, 2, 3, 4, 3, 2, 1]); //=> [3, 4, 3, 2, 1]
 */
export default curryN(2, (fn, arr = []) => {
    let idx = 0;

    while (idx < arr.length && fn(arr[idx])) {
        idx += 1;
    }

    return arr.slice(idx);
});

import curryN from '../function/curryN';

/**
 * Iterate over an input `list`, calling a provided function `fn` for each
 * element in the list.
 *
 * @param {Function} fn The function to invoke. Receives three argument: `value`, `index`, `arr`.
 * @param {Array} arr The list to iterate over.
 * @example
 *
 *      var printXPlusFive = x => console.log(x + 5);
 *      each(printXPlusFive, [1, 2, 3]);
 *      // logs 6
 *      // logs 7
 *      // logs 8
 */
export default curryN(2, (fn, arr = []) => {
    for (let i = 0; i < arr.length; i++) {
        fn(arr[i], i, arr);
    }
});

