import curryN from '../function/curryN';

/**
 * Combines two array into a set (i.e. no duplicates) composed of those
 * elements common to both arrays.
 *
 * @param {Array} a The first array.
 * @param {Array} b The second array.
 * @return {Array} The array of elements found in both `a` and `b`.
 * @example
 *
 *      intersection([1,2,3,4], [7,6,5,4,3]); //=> [4, 3]
 */
export default curryN(2, (a = [], b = []) => {
    const result = [];

    for (let i = 0; i < a.length; i++) {
        if (b.indexOf(a[i]) >= 0) {
            result.push(a[i]);
        }
    }

    return result;
});

import curryN from '../function/curryN';
import isArray from '../is/array';

/**
 * Returns a string made by inserting the `separator` between each element and
 * concatenating all the elements into a single string.
 *
 * @param {String} separator The string used to separate the elements.
 * @param {Array} arr The elements to join into a string.
 * @return {String} str The string made by concatenating `arr` with `separator`.
 * @example
 *
 *      var spacer = join(' ');
 *      spacer(['a', 2, 3.4]);   //=> 'a 2 3.4'
 *      join('|', [1, 2, 3]);    //=> '1|2|3'
 */
export default curryN(2, (separator = '', arr = []) => {
    if (!isArray(arr)) {
        arr = Array.prototype.slice.call(arr);
    }

    return arr.join(separator);
});

import curryN from '../function/curryN';

/**
 * Creates an array of values by running each element in list through fn. The fn is invoked with three arguments:
 * (value, index, arr).
 *
 * @param {Function} fn The function to be called on every element of the input `list`.
 * @param {Array} arr The list to be iterated over.
 * @return {Array} The new list.
 * @example
 *
 *      var double = x => x * 2;
 *
 *      map(double, [1, 2, 3]); //=> [2, 4, 6]
 */
export default curryN(2, (fn, arr = []) => {
    const len = arr.length;
    const result = new Array(len);

    for (let i = 0; i < len; i++) {
        result[i] = fn(arr[i], i, arr);
    }

    return result;
});

import curryN from '../function/curryN';

/**
 * Return max value in array, depending on result of calling `fn`
 *
 * @param {Function} fn The function invoked per element.
 * @param {Array} arr
 * @returns {*} Returns the maximum value.
 * @example
 *
 *      var objects = [{ 'n': 1 }, { 'n': 2 }];
 *
 *      maxBy(o => o.n, objects); // => { 'n': 2 }
 *
 */
export default curryN(2, (fn, arr = []) => {
    let result = arr[0];
    let max = fn(result, 0, arr);

    for (let i = 1; i < arr.length; i++) {
        const computed = fn(arr[i], i, arr);

        if (computed > max) {
            max = computed;
            result = arr[i];
        }
    }

    return result;
});

import curryN from '../function/curryN';

/**
 * Returns the nth element of the given array. If n is negative the
 * element at index length + n is returned.
 *
 * @param {Number} index
 * @param {*} arr
 * @return {*}
 * @example
 *
 *      var list = ['foo', 'bar', 'baz', 'quux'];
 *      nth(1, list); //=> 'bar'
 *      nth(2, list); //=> 'baz'
 *      nth(-1, list); //=> 'quux'
 *      nth(-99, list); //=> undefined
 */
export default curryN(2, (index = 0, arr = []) => {
    if (index < 0) {
        index += arr.length;
    }

    return arr[index];
});

import curryN from '../function/curryN';

/**
 * Takes a predicate and a array and returns the
 * pair of arrays of the same type of elements which do and do not
 * satisfy, the predicate, respectively.
 *
 * @param {Function} fn A predicate to determine which side the element belongs to.
 * @param {Array} arr the array to partition.
 * @return {Array} An array, containing first the subset of elements that satisfy the
 *         predicate, and second the subset of elements that do not satisfy.
 * @example
 *
 *      partition(includes('s'), ['sss', 'ttt', 'foo', 'bars']);
 *      // => [ [ 'sss', 'bars' ],  [ 'ttt', 'foo' ] ]
 */
export default curryN(2, (fn, arr = []) => {
    const t = [];
    const f = [];

    for (let i = 0; i < arr.length; i++) {
        if (fn(arr[i])) {
            t.push(arr[i]);
        } else {
            f.push(arr[i]);
        }
    }

    return [t, f];
});

import curryN from '../function/curryN';
import path from '../object/path';
import map from './map';

/**
 * Returns a new list by plucking the same named property off all objects in the list supplied.
 *
 * @param {String} key The key name to pluck off of each object.
 * @param {Array} arr The array to consider.
 * @return {Array} The list of values for the given key.
 * @example
 *
 *      pluck('a')([{a: 1}, {a: 2}]); //=> [1, 2]
 *      pluck(0)([[1, 2], [3, 4]]);   //=> [1, 3]
 */

export default curryN(2, (key, arr = []) => map(path([key]), arr));

/**
 * Creates an array of numbers (positive and/or negative) progressing from
 * `start` up to, but not including, `end`. A step of `-1` is used if a negative
 * `start` is specified without an `end` or `step`. If `end` is not specified,
 * it's set to `start` with `start` then set to `0`.
 *
 * **Note:** JavaScript follows the IEEE-754 standard for resolving
 * floating-point values which can produce unexpected results.
 *
 * @param {number} [a=0] The start of the range.
 * @param {number} b The end of the range.
 * @param {number} [step=1] The value to increment or decrement by.
 * @returns {Array} Returns the range of numbers.
 * @example
 *
 * range(4); // => [0, 1, 2, 3]
 *
 * range(-4); // => [0, -1, -2, -3]
 *
 * range(1, 5); // => [1, 2, 3, 4]
 *
 * range(0, 20, 5); // => [0, 5, 10, 15]
 *
 * range(0, -4, -1); // => [0, -1, -2, -3]
 *
 * range(0); // => []
 */
export default (a, b, step) => {
    if (typeof b !== 'number') {
        b = a;
        a = 0;
    }

    if (typeof step !== 'number') {
        step = a < b ? 1 : -1;
    }

    const length = Math.max(Math.ceil((b - a) / (step || 1)), 0);
    const result = new Array(length);
    let x = a;

    for (let i = 0; i < length; i++) {
        result[i] = x;
        x += step;
    }

    return result;
};

import curryN from '../function/curryN';

/**
 * Returns a single item by iterating through the list, successively calling
 * the iterator function and passing it an accumulator value and the current
 * value from the array, and then passing the result to the next call.
 *
 * @param {Function} fn The iterator function. Receives two values, the accumulator and the
 *        current element from the array.
 * @param {*} acc The accumulator value.
 * @param {Array} arr The list to iterate over.
 * @return {*} The final, accumulated value.
 * @example
 *
 *      var numbers = [1, 2, 3];
 *      var plus = (a, b) => a + b;
 *
 *      reduce(plus, 10, numbers); //=> 16
 */
export default curryN(3, (fn, acc, arr = []) => {
    const len = arr.length;

    for (let i = 0; i < len; i++) {
        acc = fn(acc, arr[i], i);
    }

    return acc;
});

examples:
var numbers = [1, 2, 3];
var plus = (a, b) => a + b;

reduce(plus, 10, numbers); //=> 16

