/**
 * Adds together all the elements of a list.
 *
 * @param {Array} arr An array of numbers
 * @return {Number} The sum of all the numbers in the list.
 * @example
 *
 *      sum([2,4,6,8,100,1]); //=> 121
 */
export default (arr = []) => {
    let sum = 0;

    for (let i = 0; i < arr.length; i++) {
        sum += arr[i];
    }

    return sum;
};

import curryN from '../function/curryN';

/**
 * Returns a new array|string containing the last `n` elements of a given array|string, passing
 * each value to the supplied predicate function, and terminating when the
 * predicate function returns `false`. Excludes the element that caused the
 * predicate function to fail.
 *
 * @param {Function} fn The function called per iteration.
 * @param {Array|String} arr The collection to iterate over.
 * @return {Array|String} A new array or string.
 * @example
 *
 *      var isNotOne = x => x !== 1;
 *
 *      takeRightWhile(isNotOne, [1, 2, 3, 4]); //=> [2, 3, 4]
 */
export default curryN(2, (fn, arr = []) => {
    let i = arr.length - 1;

    while (i >= 0 && fn(arr[i])) {
        i -= 1;
    }

    return arr.slice(i + 1);
});

import isArray from '../is/array';
import isArrayLike from '../is/arrayLike';
/**
 * Converts val to array. If val is array return it.
 * @param {*} val
 * @return {Array}
 * @example
 *
 *      toArray([1,2,3]) // => [1,2,3]
 *      toArray(5) // => [5]
 *      toArray('test') // => ['test']
 */
export default val => {
    if (isArray(val)) {
        return val;
    }

    if (isArrayLike(val)) {
        return Array.prototype.slice.call(val);
    }

    return [val];
};

/**
 * Returns unique items in array
 *
 * @param {Array} arr
 * @return {Array}
 *
 * @example
 *
 *  unique([1,2,1,2,3,4,1,2]) // => [1, 2, 3, 4]
 */
export default (arr = []) => {
    const result = [];

    for (let i = 0; i < arr.length; i++) {
        if (result.indexOf(arr[i]) === -1) {
            result.push(arr[i]);
        }
    }

    return result;
};

import curryN from '../function/curryN';

/**
 * Returns unique items in array. Uniqueness is defined by `fn`.
 *
 * @param {Function} fn
 * @param {Array} arr
 * @return {Array}
 *
 * @example
 *
 *      uniqBy(x => x % 2, [1, 2, 2, 3, 4, 5, 5, 5]) // [1, 2]
 */
export default curryN(2, (fn, arr = []) => {
    const result = [];
    const unq = Object.create(null);

    for (let i = 0; i < arr.length; i++) {
        const key = fn(arr[i]);

        if (!unq[key]) {
            result.push(arr[i]);
            unq[key] = true;
        }
    }

    return result;
});

import adjust from './adjust';
import curryN from '../function/curryN';
import always from '../function/always';

/**
 * Returns a new copy of the array with the element at the provided index
 * replaced with the given value.
 *
 * @param {Number} idx index
 * @param {*} x The value to exist at the given index of the returned array.
 * @param {Array} list The source array to be updated.
 * @return {Array} A copy of `list` with the value at index `idx` replaced with `x`.
 * @example
 *
 *      update(1, 11, [0, 1, 2]);     //=> [0, 11, 2]
 *      update(1)(11)([0, 1, 2]);     //=> [0, 11, 2]
 */

export default curryN(3, (idx, x, list) => (
    adjust(always(x), idx, list)
));

import reject from './reject';
import curryN from '../function/curryN';
import flip from '../function/flip';
import includes from './includes';

/**
 * Returns a new list without values in the first argument.
 *
 * @param {Array} xs The values to be removed from `list2`.
 * @param {Array} list The array to remove values from.
 * @return {Array} The new array without values in `list1`.
 * @example
 *
 *      without([1, 2], [1, 2, 1, 3, 4]); //=> [3, 4]
 */
export default curryN(2, (xs, list) => reject(flip(includes)(xs), list));

import curryN from '../function/curryN';

/**
 * Creates a new list out of the two supplied by pairing up equally-positioned
 * items from both lists. The returned list is truncated to the length of the
 * shorter of the two input lists.
 *
 * @param {Array} a The first array to consider.
 * @param {Array} b The second array to consider.
 * @return {Array} The list made by pairing up same-indexed elements of `a` and `b`.
 * @example
 *
 *      zip([1, 2, 3], ['a', 'b', 'c']); //=> [[1, 'a'], [2, 'b'], [3, 'c']]
 */
export default curryN(2, (a = [], b = []) => {
    const len = Math.min(a.length, b.length);
    const result = new Array(len);

    for (let i = 0; i < len; i++) {
        result[i] = [a[i], b[i]];
    }

    return result;
});

import curryN from '../function/curryN';

/**
 * Creates a new list out of the two supplied by applying the function to each
 * equally-positioned pair in the lists. The returned list is truncated to the
 * length of the shorter of the two input lists.
 *
 * @param {Function} fn The function used to combine the two elements into one value.
 * @param {Array} a The first array to consider.
 * @param {Array} b The second array to consider.
 * @return {Array} The list made by combining same-indexed elements of `a` and `b`
 *         using `fn`.
 * @example
 *
 *      var f = (x, y) => {
 *        // ...
 *      };
 *      zipWith(f, [1, 2, 3], ['a', 'b', 'c']);
 *      //=> [f(1, 'a'), f(2, 'b'), f(3, 'c')]
 */
export default curryN(3, (fn, a = [], b = []) => {
    const len = Math.min(a.length, b.length);
    const result = new Array(len);

    for (let i = 0; i < len; i++) {
        result[i] = fn(a[i], b[i]);
    }

    return result;
});

import type from './type';
import mapObj from './object/map';
import mapArr from './array/map';

const cloneRegExp = pattern =>
    new RegExp(pattern.source, (pattern.global ? 'g' : '')
        + (pattern.ignoreCase ? 'i' : '')
        + (pattern.multiline ? 'm' : '')
        + (pattern.sticky ? 'y' : '')
        + (pattern.unicode ? 'u' : ''));

/**
 * Creates a deep copy of the value which may contain (nested) `Array`s and
 * `Object`s, `Number`s, `String`s, `Boolean`s and `Date`s. `Function`s are not
 * copied, but assigned by their reference.
 *
 * @param {*} x The value to clone
 * @return {*} A copy of a value.
 * @example
 *
 *      var objects = [{}, {}, {}];
 *      var objectsClone = clone(objects);
 *      objects[0] === objectsClone[0]; //=> false
 */
const clone = x => {
    switch (type(x)) {
        case 'Object':
            return mapObj(clone, x);
        case 'Array':
            return mapArr(clone, x);
        case 'Date':
            return new Date(x.valueOf());
        case 'RegExp':
            return cloneRegExp(x);
        default:
            return x;
    }
};

export default clone;

examples:
var objects = [{}, {}, {}];
var objectsClone = clone(objects);
objects[0] === objectsClone[0]; //=> false

