/**
 * Returns a function, `fn`, which encapsulates if/else-if/else logic.
 * `cond` takes a list of [predicate, transform] pairs. All of the arguments
 * to `fn` are applied to each of the predicates in turn until one returns a
 * "truthy" value, at which point `fn` returns the result of applying its
 * arguments to the corresponding transformer. If none of the predicates
 * matches, `fn` returns undefined.
 *
 * @param {[Function, Function]} pairs
 * @return {Function}
 * @example
 *
 *      var fn = cond([
 *        [isEqual(0),   always('water freezes at 0C')],
 *        [isEqual(100), always('water boils at 100C')],
 *        [T,           temp => 'nothing special happens at ' + temp + 'C']
 *      ]);
 *      fn(0); //=> 'water freezes at 0C'
 *      fn(50); //=> 'nothing special happens at 50C'
 *      fn(100); //=> 'water boils at 100C'
 */
export default pairs => function(...args) {
    for (let i = 0; i < pairs.length; i++) {
        if (pairs[i][0].apply(this, args)) {
            return pairs[i][1].apply(this, args);
        }
    }
};

import curryN from './curryN';

/**
 * Returns a curried equivalent of the provided function. The arguments of curried function
 * needn't be provided one at a time. If `f` is a ternary function and `g` is `curry(f)`, the
 * following are equivalent:
 *
 *   - `g(1)(2)(3)`
 *   - `g(1)(2, 3)`
 *   - `g(1, 2)(3)`
 *   - `g(1, 2, 3)`
 *
 * @param {Function} fn The function to curry.
 * @return {Function} A new, curried function.
 * @example
 *
 *      var addFourNumbers = (a, b, c, d) => a + b + c + d;
 *
 *      var curriedAddFourNumbers = curry(addFourNumbers);
 *      var f = curriedAddFourNumbers(1, 2);
 *      var g = f(3);
 *      g(4); //=> 10
 */
export default fn => curryN(fn.length, fn);

examples:
var addFourNumbers = (a, b, c, d) => a + b + c + d;

var curriedAddFourNumbers = curry(addFourNumbers);
var f = curriedAddFourNumbers(1, 2);
var g = f(3);
g(4); //=> 10

