import curryN from './curryN';

/**
 * Creates a debounced function that delays invoking `func` until after `wait`
 * milliseconds have elapsed since the last time the debounced function was
 * invoked. Delayed function invocation might be cancelled by calling cancel method.
 *
 * @param {number} wait The number of milliseconds to delay.
 * @param {Function} fn The function to debounce.
 * @returns {Function} Returns the new debounced function.
 */
export default curryN(2, (wait, fn) => {
    let timeout;

    function f() {
        clearTimeout(timeout);
        timeout = setTimeout(
            () => fn.apply(this, arguments),  // eslint-disable-line prefer-rest-params
            wait
        );
    }

    f.cancel = () => clearTimeout(timeout);

    return f;
});

examples:
var gt10 = x => x > 10;
var even = x => x % 2 === 0;
var f = either(gt10, even);
f(101); //=> true
f(8); //=> true
f(3); //=> false

identity(1); //=> 1

const obj = {};
identity(obj) === obj; //=> true

const prop = ifElse(
  x => x.a,
  x => x.b,
  x => x.c
);
prop({ a: false, c: 2 }); //=> 2
prop({ a: true, b: 1, c: 2 }); //=> 1

