import curryN from './curryN';

/**
 * Creates a throttled function that only invokes `fn` at most once per
 * every `wait` milliseconds. `fn` is called in start of `wait` delay
 *
 * @param {number} wait The number of milliseconds to throttle invocations to.
 * @param {Function} fn The function to throttle.
 */
export default curryN(2, (wait, fn) => {
    let lastCalled;
    let timeout;

    return function(...args) {
        const now = Date.now();
        const diff = lastCalled + wait - now;

        if (lastCalled && diff > 0) {
            clearTimeout(timeout);
            timeout = setTimeout(() => {
                lastCalled = now;
                fn.apply(this, args);
            }, diff);
        } else {
            lastCalled = now;
            fn.apply(this, args);
        }
    };
});

import curryN from './curryN';

/**
 * Creates a throttled function that only invokes `fn` at most once per
 * every `wait` milliseconds. `fn` is called in the end of `wait` delay
 *
 * @param {number} wait The number of milliseconds to throttle invocations to.
 * @param {Function} fn The function to throttle.
 */
export default curryN(2, (wait, fn) => {
    let lastCalled;
    let timeout;

    return function(...args) {
        const now = Date.now();
        const diff = lastCalled + wait - now;

        if (diff < 0 && timeout) {
            clearTimeout(timeout);
            timeout = null;
            fn.apply(this, args);
        } else if (!timeout) {
            timeout = setTimeout(() => {
                fn.apply(this, args);
                timeout = null;
            }, wait);
        }

        lastCalled = now;
    };
});

import curryN from './curryN';

/**
 * Takes two functions, tryer and catcher.
 *
 * Returns a function that, when invoked with zero or more parameters,
 * calls the tryer with those parameters, and if tryer throws,
 * calls the catcher with the error as first argument and original arguments as rest.
 * If that still throws, then \_()_/
 *
 * @param {Function} tryer
 * @param {Function} catcher
 * @return {Function}
 * @example
 *      tryCatch(x => x.length, () => 0)([ 1, 2, 3 ]) // 3
 *      tryCatch(x => x.length, () => 0)( undefined ) // 0
 *      tryCatch(
 *          someDataTransform,
 *          (err, ...rest) => {
 *              logAsyncToServer('someDataTransform failed', err, 'with arguments', rest);
 *              return DEFAULT_VALUE;
 *          }
 *      )( someIncompleteData ) // DEFAULT_VALUE (error is logged somewhere)
 */
export default curryN(2, (tryer, catcher) => {
    return (...args) => {
        try {
            return tryer.apply(this, args);
        } catch (e) {
            args.unshift(e);
            return catcher.apply(this, args);
        }
    };
});

examples:
tryCatch(x => x.length, () => 0)([ 1, 2, 3 ]) // 3
tryCatch(x => x.length, () => 0)( undefined ) // 0
tryCatch(
    someDataTransform,
    (err, ...rest) => {
        logAsyncToServer('someDataTransform failed', err, 'with arguments', rest);
        return DEFAULT_VALUE;
    }
)( someIncompleteData ) // DEFAULT_VALUE (error is logged somewhere)

