import emptyRamda from 'ramda/src/empty';
import emptyLodash from 'lodash/isEmpty';
import empty from '../empty';

const tests = [
    null,
    undefined,
    {},
    { a: 1 },
    [1, 2, 3],
    [],
    '',
    'abc'
];

export default {
    ramda: () => {
        tests.forEach(x => emptyRamda(x));
    },
    lodash: () => {
        tests.forEach(x => emptyLodash(x));
    },
    utils: () => {
        tests.forEach(x => empty(x));
    }
};

import isArray from '../array';

describe('utils/is/array', () => {
    it('test', () => {
        expect(isArray([])).toBe(true);
        expect(isArray([1, 2, 3])).toBe(true);
        expect(isArray('test')).toBe(false);
        expect(isArray(null)).toBe(false);
    });
});

import isArrayLike from '../arrayLike';

describe('utils/is/arrayLike', () => {
    it('test', () => {
        expect(isArrayLike(5)).toBe(false);
        expect(isArrayLike([])).toBe(true);
        expect(isArrayLike({})).toBe(false);
        expect(isArrayLike('fawfawf')).toBe(false);
        expect(isArrayLike({ length: 0 })).toBe(true);
        expect(isArrayLike({ length: 10 })).toBe(false);
        expect(isArrayLike({ 0: 5, 9: 3, length: 10 })).toBe(true);
    });
});

import isBoolean from '../boolean';

describe('utils/is/boolean', () => {
    it('test', () => {
        expect(isBoolean(true)).toBe(true);
        expect(isBoolean(false)).toBe(true);
        expect(isBoolean(new Boolean('true'))).toBe(true); // eslint-disable-line no-new-wrappers
        expect(isBoolean('')).toBe(false);
        expect(isBoolean(5)).toBe(false);
        expect(isBoolean(null)).toBe(false);
    });
});

import contains from '../contains';

describe('contains', () => {
    const fn = () => {};

    it('true', () => {
        expect(contains(3, [1, 2, 3])).toBeTruthy();
        expect(contains('test', ['test', 2, 3])).toBeTruthy();
        expect(contains(true, ['test', true, 3])).toBeTruthy();
        expect(contains(null, ['test', true, null])).toBeTruthy();
        expect(contains(NaN, ['test', true, NaN])).toBeTruthy();
        expect(contains([42], [[42], { test: 'test' }, 3])).toBeTruthy();
        expect(contains({ test: 'test' }, [[42], { test: 'test' }, 3])).toBeTruthy();
        expect(contains(fn, [fn, 2, 3])).toBeTruthy();
        expect(contains(undefined, [1, 2, undefined])).toBeTruthy();
    });

    it('false', () => {
        expect(contains(3, [1, 2])).toBeFalsy();
        expect(contains('test', [2, 3])).toBeFalsy();
        expect(contains(true, ['test', 3])).toBeFalsy();
        expect(contains(null, ['test', true])).toBeFalsy();
        expect(contains(NaN, ['test', true])).toBeFalsy();
        expect(contains([42], [{ test: 'test' }, 3])).toBeFalsy();
        expect(contains({ test: 'test' }, [[42], 3])).toBeFalsy();
        expect(contains(fn, [2, 3])).toBeFalsy();
        expect(contains(undefined, [1, 2])).toBeFalsy();
    });
});

import isElement from '../element';

describe('utils/is/element', () => {
    it('should return true for dom elements', () => {
        expect(isElement(document.createElement('div'))).toBe(true);
        expect(isElement(document.body)).toBe(true);
    });

    it('should return false otherwise', () => {
        expect(isElement(5)).toBe(false);
        expect(isElement(undefined)).toBe(false);
        expect(isElement(null)).toBe(false);
        expect(isElement({})).toBe(false);
        expect(isElement({ nodeType: 1 })).toBe(false);
        expect(isElement('<div></div>')).toBe(false);
        expect(isElement('nodeType=1')).toBe(false);
        expect(isElement(/123/)).toBe(false);
        expect(isElement(new Date())).toBe(false);
    });
});

import isEmpty from '../empty';

describe('utils/is/empty', () => {
    it('test', () => {
        expect(isEmpty(undefined)).toBe(true);
        expect(isEmpty(null)).toBe(true);
        expect(isEmpty([])).toBe(true);
        expect(isEmpty('')).toBe(true);
        expect(isEmpty({})).toBe(true);
        expect(isEmpty({ a: 5 })).toBe(false);
        expect(isEmpty(5)).toBe(false);
        expect(isEmpty([1])).toBe(false);
        expect(isEmpty('t')).toBe(false);
    });
});

import isEqual from '../equal';

describe('utils/is/equal', () => {
    it('equal primitives', () => {
        expect(isEqual(1, 2)).toBe(false);
        expect(isEqual(1, 1)).toBe(true);
        expect(isEqual('test', 'test2')).toBe(false);
        expect(isEqual('ttt', 'ttt')).toBe(true);
        expect(isEqual(true, false)).toBe(false);
        expect(isEqual(true, true)).toBe(true);
        expect(isEqual(null, null)).toBe(true);
    });

    it('equal arrays', () => {
        expect(isEqual([1, 2, 3], [1, 2, 3])).toBe(true);
        expect(isEqual([1, 2, 3], [1, 2])).toBe(false);
        expect(isEqual([], [])).toBe(true);
        expect(isEqual([3, 2, 1], [1, 2, 3])).toBe(false);
        expect(isEqual([[1, 2, [[3]]]], [[1, 2, [[3]]]])).toBe(true);
    });

    it('equal internal types', () => {
        expect(isEqual(new Date(), new Date())).toBe(true);
        expect(isEqual(new Date(), new Date(123))).toBe(false);
        expect(isEqual(/123/, /123/)).toBe(true);
        expect(isEqual(/123/, /1234/)).toBe(false);
        expect(isEqual(() => 3, () => 3)).toBe(true);
        expect(isEqual(() => 3, () => 4)).toBe(false);
    });

    it('equal objects', () => {
        expect(isEqual({}, {})).toBe(true);
        expect(isEqual({ a: 1 }, { a: 1 })).toBe(true);
        expect(isEqual({ a: 1 }, { a: 2 })).toBe(false);
        expect(isEqual({ a: 1, b: [1, 2, { c: 3 }] }, { a: 1, b: [1, 2, { c: 3 }] })).toBe(true);
        expect(isEqual({ a: undefined }, { b: undefined })).toBe(false);
        expect(isEqual({}, null)).toBe(false);
        expect(isEqual({ a: 1, b: undefined }, { a: 1, c: 2 })).toBe(false);
    });

    it('different types', () => {
        expect(isEqual({}, [])).toBe(false);
        expect(isEqual({}, /23/)).toBe(false);
        expect(isEqual({}, 5)).toBe(false);
        expect(isEqual(3, 'test')).toBe(false);
    });
});

import isFinite from '../finite';

describe('utils/is/finite', () => {
    it('check is finite', () => {
        expect(isFinite(0)).toBe(true);
        expect(isFinite(21424.2424)).toBe(true);
        expect(isFinite(2e64)).toBe(true);
        expect(isFinite(new Number(8))).toBe(true); // eslint-disable-line no-new-wrappers

        expect(isFinite(NaN)).toBe(false);
