import isArray from './array';
import isString from './string';
import has from '../object/has';

/**
 * Checks if `test` is arrayLike (has `length` and index properties).
 *
 * @param {*} test The value to check.
 * @returns {boolean}
 * @example
 *
 * isArrayLike([]); // => true
 * isArrayLike({ 0:'a', 1: 'b', length: 2 }); // => true
 * isArrayLike(null); // => false
 */
export default test => {
    switch (true) {
        case isArray(test):
            return true;
        case !test:
        case typeof test !== 'object':
        case isString(test):
            return false;
        case test.nodeType === 1:
            return !!test.length;
        case test.length === 0:
            return true;
        case test.length > 0:
            return has(0, test) && has(test.length - 1, test);
    }

    return false;
};

import equals from './equal';
import curryN from '../function/curryN';

// Array.prototype.indexOf doesn't exist below IE9
// manually crawl the list to distinguish between +0 and -0
// NaN
// non-zero numbers can utilise Set
// all these types can utilise Set
// null can utilise Set
// anything else not covered above, defer to R.equals
const indexOf = (list, a, idx) => { // eslint-disable-line max-statements, complexity
    // Array.prototype.indexOf doesn't exist below IE9
    if (typeof list.indexOf === 'function') {
        switch (typeof a) {
            case 'number':
                const inf = 1 / a;
                let item;

                if (a === 0) {
                    // manually crawl the list to distinguish between +0 and -0
                    while (idx < list.length) {
                        item = list[idx];
                        if (item === 0 && 1 / item === inf) {
                            return idx;
                        }
                        idx += 1;
                    }
                    return -1;
                } else if (a !== a) { // eslint-disable-line no-self-compare
                    // NaN
                    while (idx < list.length) {
                        item = list[idx];
                        if (typeof item === 'number' && item !== item) { // eslint-disable-line no-self-compare
                            return idx;
                        }
                        idx += 1;
                    }
                    return -1;
                }
                // non-zero numbers can utilise Set
                return list.indexOf(a, idx);
            // all these types can utilise Set
            case 'string':
            case 'boolean':
            case 'function':
            case 'undefined':
                return list.indexOf(a, idx);
            case 'object':
                if (a === null) {
                    // null can utilise Set
                    return list.indexOf(a, idx);
                }
        }
    }
    // anything else not covered above, defer to R.equals
    while (idx < list.length) {
        if (equals(list[idx], a)) {
            return idx;
        }
        idx += 1;
    }
    return -1;
};

const contains = (a, list) => indexOf(list, a, 0) > -1;

/**
 * Returns `true` if the specified value is equal, in `is/equal` terms, to at
 * least one element of the given list; `false` otherwise.
 *
 * @param {Object} a The item to compare against.
 * @param {Array} list The array to consider.
 * @return {Boolean} `true` if the item is in the list, `false` otherwise.
 * @example
 *
 *      contains(3, [1, 2, 3]); //=> true
 *      contains(4, [1, 2, 3]); //=> false
 *      contains([42], [[42]]); //=> true
 */
export default curryN(2, contains);

import isNil from './nil';
import isArray from './array';
import isString from './string';
import isObject from './object';

/**
 * Checks if `test` is empty.
 *
 * @param {*} test The value to check.
 * @returns {boolean} Returns `true` if `test` is empty, else `false`.
 * @example
 *
 * isEmpty([]); // => true
 * isEmpty(null); // => true
 * isEmpty({}); // => true
 * isEmpty(''); // => true
 * isEmpty('test'); // => false
 * isEmpty({ a: 5 }); // => false
 * isEmpty([1]); // => false
 */
export default test => {
    switch (true) {
        case isNil(test):
            return true;
        case isString(test):
            return test === '';
        case isArray(test):
            return test.length === 0;
        case isObject(test):
            return Object.keys(test).length === 0;
    }

    return false;
};

import curry from '../function/curry';
import isArray from './array';

const isEqualNativeTypes = (test1, test2) =>
    test1.toString() === test2.toString();

const isEqualArrays = (test1, test2) => {
    const len = test1.length;

    if (len !== test2.length) {
        return false;
    }

    for (let i = 0; i < len; i++) {
        if (!isEqual(test1[i], test2[i])) {
            return false;
        }
    }

    return true;
};

const isEqualObjects = (test1, test2) => {
    const keys = Object.keys(test1);
    const len = keys.length;

    if (len !== Object.keys(test2).length) {
        return false;
    }

    for (let i = 0; i < len; i++) {
        const key = keys[i];

        if (!(Object.prototype.hasOwnProperty.call(test2, key) && isEqual(test1[key], test2[key]))) {
            return false;
        }
    }

    return true;
};

/**
 * Returns `true` if its arguments are equivalent, `false` otherwise.
 *
 * @param {*} test1
 * @param {*} test2
 * @return {Boolean}
 * @example
 *
 *      isEqual(1, 1); //=> true
 *      isEqual(1, '1'); //=> false
 *      isEqual([1, 2, 3], [1, 2, 3]); //=> true
 *      isEqual({ a: { b: 1 }}, { a: { b: 1 }}); //=> true
 */
const isEqual = (test1, test2) => {
    if (test1 === test2) {
        return true;
    }

    if (typeof test1 !== typeof test2
        || test1 !== Object(test1)
        || !test1
        || !test2) {
        return false;
    }

    if (isArray(test1) && isArray(test2)) {
        return isEqualArrays(test1, test2);
    }

    const test1ToString = Object.prototype.toString.call(test1);

    if (test1ToString === '[object Object]'
        && Object.prototype.toString.call(test2) === test1ToString) {
        return isEqualObjects(test1, test2);
    }

    return isEqualNativeTypes(test1, test2);
};

export default curry(isEqual);

import isReactElement from './reactElement';
import isObject from './object';

/**
 * Returns whether a value is a plain object
 * (an object that is created using an object literal, Object.create(null) or similar).
 * Rejects anything with a custom prototype or a non-object ECMAScript type.
 * Also rejects React elements
 *
 * **Note:** if the host environment does not support Symbol, any object with a $$typeof
 * property is considered a React element
 *
 * @param {*} test The value to check
 * @returns {boolean} Returns `true` if `test` is a plain object, else `false`.
 * @example
 *
 * isPlainObject({ a: 'test' }); // => true
 *
 * isPlainObject(moment()); // => false
 *
 * isPlainObject(<span></span>); // => false
 */
export default function isPlainObject(test) {
    if (!isObject(test)) {
        return false;
    }

    const prototype = Object.getPrototypeOf(test);

    if (prototype === null) {
        // objects created with Object.create(null) are still plain objects
        return true;
    }

    if (prototype !== Object.prototype) {
        // has a custom prototype, probably was created with a custom constructor,
        // may contain internal data that's not meant to be accessed from outside
        return false;
    }

    if (isReactElement(test)) {
        // react elements _are_ plain objects, but we don't treat them like that
        // (e.g. in recursive merges)
        return false;
    }

    return true;
}

import isObject from './object';
import isFunction from './function';

/**
 * Checks if `test` is Promise.
 *
 * @param {*} test The value to check.
 * @returns {boolean} Returns `true` if `test` is Promise, else `false`.
 * @example
 *
 *      isPromise({}) // => false
 *      isPromise(Promise.resolve()) // => true
 */
export default test => (isObject(test) || isFunction(test)) && isFunction(test.then);

import has from '../object/has';

const HAS_SYMBOL_SUPPORT = typeof Symbol === 'function' && typeof Symbol.for === 'function';
const REACT_ELEMENT_TYPE = HAS_SYMBOL_SUPPORT && Symbol.for('react.element');

/**
 * Returns whether a value is a valid React element
 *
 * **Note:** this won't work with any API-compatible libraries like Inferno
 * or Preact which check virtual DOM node integrity through other means
 * (binary flags and instanceof checks respectively)
 *
 * **Note:** if the host environment does not support Symbol, any $$typeof
 * property value is considered valid.
 *
 * @param {*} test a reference being tested
 * @returns whether a value is a React element
 */
export default function isReactElement(test) {
    return !!test && has('$$typeof', test) && (
        !HAS_SYMBOL_SUPPORT || test.$$typeof === REACT_ELEMENT_TYPE
    );
}

/**
 * Negates its argument.
 *
 * @param {Number} n
 * @return {Number}
 * @example
 *
 *      negate(42); //=> -42
 */
export default n => -n;

