describe('utils/object/pickBy', () => {
    it('should return new object with properties accorindg predicate', () => {
        expect(pickBy(() => false, { a: 1, b: 2 })).toEqual({});
        expect(pickBy(() => true, { a: 1, b: 2 })).toEqual({ a: 1, b: 2 });
        expect(pickBy(x => x > 1)({ a: 1, b: 2 })).toEqual({ b: 2 });
    });
});

import prop from '../prop';

describe('utils/object/prop', () => {
    it('should return undefined if prop is not set', () => {
        expect(prop('test', undefined)).toBeUndefined();
        expect(prop('test', null)).toBeUndefined();
        expect(prop('test', '123')).toBeUndefined();
        expect(prop('test', { a: 1, b: 2 })).toBeUndefined();
        expect(prop('test', [1, 2, 3])).toBeUndefined();
    });

    it('should return prop', () => {
        expect(prop('test', { test: 'a', test1: 'b' })).toBe('a');

        function A() {}
        A.prototype.test = 'c';
        expect(prop('test', new A())).toBe('c');
    });
});

import identity from '../../function/identity';
import propApply from '../propApply';

describe('utils/object/propApply', () => {
    it('should apply func to prop value', () => {
        const f = jest.fn(identity);

        expect(propApply('a', f, { a: 5 })).toBe(5);
        expect(f).toBeCalledWith(5);

        expect(propApply('a', f, {})).toBeUndefined();
        expect(f).toBeCalledWith(undefined);
    });
});

import propEq from '../propEq';

describe('utils/object/propEq', () => {
    it('should return true if prop is equal to value', () => {
        expect(propEq('a', 1, { a: 1 })).toBe(true);
        function A() {}
        A.prototype.a = 5;
        expect(propEq('a', 5, new A())).toBe(true);
    });

    it('should return false otherwise', () => {
        expect(propEq('a', 1, { a: 2, b: 3 })).toBe(false);
        expect(propEq('a', 1, {})).toBe(false);
        expect(propEq('a', 1, undefined)).toBe(false);
        expect(propEq('a', 1, '123')).toBe(false);
    });
});

import propOr from '../propOr';

describe('utils/object/propOr', () => {
    it('should return prop', () => {
        const f = propOr('a', 'wrong');

        expect(f({ a: 1 })).toBe(1);
        expect(f({ a: 2, b: 3 })).toBe(2);

        function A() {}
        A.prototype.a = 'test123';
        expect(f(new A())).toBe('test123');
    });

    it('should return default value', () => {
        const f = propOr('a', 'dflt');

        expect(f({})).toBe('dflt');
        expect(f(null)).toBe('dflt');
        expect(f(undefined)).toBe('dflt');
        expect(f('awfwf')).toBe('dflt');
        expect(f({ dflt: 5 })).toBe('dflt');
    });
});

import reduce from '../reduce';

describe('utils/object/reduce', () => {
    it('reduce for objects', () => {
        const f = jest.fn((a, v, k) => a + v);
        const obj = { a: 1, b: 2, c: 3 };

        expect(reduce(f, 1, obj)).toBe(7);
        expect(f).toBeCalledWith(1, 1, 'a');
        expect(f).toBeCalledWith(2, 2, 'b');
        expect(f).toBeCalledWith(4, 3, 'c');

        expect(reduce(f, 8, {})).toBe(8);
    });
});

import size from '../size';

describe('utils/object/size', () => {
    it('test', () => {
        expect(size({})).toBe(0);
        expect(size({ a: 1, b: 2 })).toBe(2);
        expect(size({ a: 1, b: 3, c: 9 })).toBe(3);
    });
});

import toPairs from '../toPairs';

describe('utils/object/toPairs', () => {
    it('should return array', () => {
        expect(toPairs({ a: 1, b: 2, c: 3 })).toEqual([['a', 1], ['b', 2], ['c', 3]]);
    });
});

import values from '../values';

describe('utils/object/values', () => {
    it('test', () => {
