        expect(values({})).toEqual([]);
    });
});

import where from '../where';
import isEqual from '../../is/equal';

describe('utils/object/where', () => {
    it('should return true/false by spec object', () => {
        const testObj = { a: 1, b: 2, c: 3 };

        expect(where({ a: isEqual(1), b: isEqual(2) }, testObj)).toBe(true);
        expect(where({ a: isEqual(1), b: isEqual(2), c: isEqual(4) }, testObj)).toBe(false);
        expect(where({ c: isEqual(3) }, testObj)).toBe(true);
        expect(where({ a: isEqual(1), b: isEqual(2), c: isEqual(3) }, testObj)).toBe(true);
    });
});

import curryN from '../function/curryN';

/**
 * Tests whether every [key, value] pair in the object passes the test implemented by the provided function.
 *
 * @param {Function} fn The function to test for each [key, value] pair. Takes a predicate and an object.
 * @param {Object} obj The object whose enumerable own property [key, value] pairs are to be tested.
 * @return {Boolean} Returns `true` if the callback function returns a truthy value for every [key, value] pair, otherwise, false.
 * @example
 *
 *      var isBiggerThanZero = x => x > 0;
 *
 *      all(isBiggerThanZero, {}); //=> true
 *      all(isBiggerThanZero, { a: 1 }); //=> true
 *      all(isBiggerThanZero, { a: 1, b: 1, c: 1 }); //=> true
 *      all(isBiggerThanZero, { a: 0, b: 1, c: 0 }); //=> false
 *      all(isBiggerThanZero, { a: 1, b: 0, c: 1 }); //=> false
 *      all(isBiggerThanZero)({ a: 0, b: 0, c: 0 }); //=> false
 *      all(isBiggerThanZero)({ a: 1, b: 0, c: 1 }); //=> false
 */
export default curryN(2, (fn, obj = {}) => {
    for (const key in obj) {
        if (Object.prototype.hasOwnProperty.call(obj, key) && !fn(obj[key], key, obj)) {
            return false;
        }
    }
    return true;
});

import curryN from '../function/curryN';

/**
 * Tests whether at least one [key, value] pair in the object passes the test implemented by the provided function.
 *
 * @param {Function} fn The function to test for each [key, value] pair. Takes a predicate and an object.
 * @param {Object} obj The object whose enumerable own property [key, value] pairs are to be tested.
 * @return {Boolean} Returns `true` if the callback function returns a truthy value for any [key, value] pair, otherwise, false.
 * @example
 *
 *      var isBiggerThanZero = x => x > 0;
 *
 *      any(isBiggerThanZero, {}); //=> false
 *      any(isBiggerThanZero, { a: 0 }); //=> false
 *      any(isBiggerThanZero, { a: 0, b: 1, c: 0 }); //=> true
 *      any(isBiggerThanZero, { a: 1, b: 0, c: -1 }); //=> true
 *      any(isBiggerThanZero)({ a: 0, b: 0, c: 0 }); //=> false
 *      any(isBiggerThanZero)({ a: 0, b: 1, c: 0 }); //=> true
 */
export default curryN(2, (fn, obj = {}) => {
    for (const key in obj) {
        if (Object.prototype.hasOwnProperty.call(obj, key) && fn(obj[key], key, obj)) {
            return true;
        }
    }
    return false;
});

import curryN from '../function/curryN';

/**
 * Iterate over an input `object`, calling a provided function `fn` for each
 * key and value in the object.
 *
 * @param {Function} fn The function to invoke. Receives three argument, `value`, `key`, `obj`.
 * @param {Object} obj The object to iterate over.
 * @return {Object} The original object.
 * @example
 *
 *      var printKeyConcatValue = (value, key) => console.log(key + ':' + value);
 *      each(printKeyConcatValue, {x: 1, y: 2}); //=> {x: 1, y: 2}
 *      // logs x:1
 *      // logs y:2
 */
export default curryN(2, (fn, obj = {}) => {
    for (const key in obj) {
        if (Object.prototype.hasOwnProperty.call(obj, key)) {
            fn(obj[key], key, obj);
        }
    }
});

