import curryN from '../function/curryN';
import objectKeys from './keys';

/**
 * Takes a predicate and a object, and returns a new object
 * containing only members which satisfy the
 * given predicate.
 *
 * @param {Function} fn
 * @param {Object} obj
 * @return {Object}
 * @example
 *
 *      var isEven = n => n % 2 === 0;
 *
 *      filter(isEven, {a: 1, b: 2, c: 3, d: 4}); //=> {b: 2, d: 4}
 */
export default curryN(2, (fn, obj = {}) => {
    const keys = objectKeys(obj);
    const result = {};

    for (let i = 0; i < keys.length; i++) {
        const key = keys[i];

        if (fn(obj[key], key, obj)) {
            result[key] = obj[key];
        }
    }

    return result;
});

import curryN from '../function/curryN';

/**
 * Returns first key in `obj` satisfied to `fn`, or undefined if there is no such keys
 *
 * @param {Function} fn Receives three argument, `value`, `key`, `obj`
 * @param {Object} obj
 * @returns {String|undefined}
 *
 * @example
 *
 *      findKey(identity, {a: 0, b: 3}) // => 'b'
 *      findKey(x => x > 3, { a: 2, b: 1 }) // => undefined
 *      findKey(x => x > 3, { a: 4, b: 5 }) // => 'a'
 */
export default curryN(2, (fn, obj = {}) => {
    for (const key in obj) {
        if (Object.prototype.hasOwnProperty.call(obj, key)) {
            if (fn(obj[key], key, obj)) {
                return key;
            }
        }
    }
});

/**
 * Creates a new object from a list key-value pairs. If a key appears in
 * multiple pairs, the rightmost pair is included in the object.
 *
 * @param {Array} arr An array of two-element arrays that will be the keys and values of the output object.
 * @return {Object} The object made by pairing up `keys` and `values`.
 * @example
 *
 *      fromPairs([['a', 1], ['b', 2], ['c', 3]]); //=> {a: 1, b: 2, c: 3}
 */
export default (arr = []) => {
    const result = {};

    for (let i = 0; i < arr.length; i++) {
        result[arr[i][0]] = arr[i][1];
    }

    return result;
};

import curryN from '../function/curryN';
import objectKeys from './keys';

/**
 * Creates an object composed of keys generated from the results of running
 * each element of object thru `fn`. The corresponding
 * value of each key is an array of elements responsible for generating the key.
 *
 * @param {Function} fn The function to transform value to group key. Receives three argument, `value`, `key`, `obj`.
 * @param {Object} obj The obj to iterate over.
 * @returns {Object} Returns the composed aggregate object.
 * @example
 *
 * groupBy(x => x, { a: 1, b: 1, c: 3});// => { '1': [1, 1], '3': [3] }
 */
export default curryN(2, (fn, obj = {}) => {
    const result = {};
    const keys = objectKeys(obj);

    for (let i = 0; i < keys.length; i++) {
        const key = keys[i];
        const groupValue = fn(obj[key], key, obj);

        if (!result[groupValue]) {
            result[groupValue] = [];
        }

        result[groupValue].push(obj[key]);
    }

    return result;
});

import curryN from '../function/curryN';
import objectKeys from './keys';

/**
 * Creates an object composed of keys generated from the results of running
 * each element of object thru `fn`. The corresponding value of
 * each key is the last element responsible for generating the key
 *
 * @param {Function} fn The function to transform value to group key. Receives three argument, `value`, `key`, `obj`.
 * @param {Object} obj The obj to iterate over.
 * @returns {Object} Returns the composed aggregate object.
 * @example
 *
 * keyBy(x => x < 2, { a: 0, b: 1, c: 3});// => { false: 1, true: 3 }
 */

export default curryN(2, (fn, obj = {}) => {
    const result = {};
    const keys = objectKeys(obj);

    for (let i = 0; i < keys.length; i++) {
        const key = keys[i];
        const groupValue = fn(obj[key], key, obj);

        result[groupValue] = obj[key];
    }

    return result;
});

import curryN from '../function/curryN';

/**
 * Applies `fn` to each of the `obj` values, and returns new object.
 *
 * @param {Function} fn The function to be called on every value of the input `obj`. Receives three argument, `value`, `key`, `obj`.
 * @param {Object} obj The object to be iterated over.
 * @return {Object} The new object.
 * @example
 *
 *      var double = x => x * 2;
 *
 *      map(double, {x: 1, y: 2, z: 3}); //=> {x: 2, y: 4, z: 6}
 */
export default curryN(2, (fn, obj = {}) => {
    const result = {};

    for (const key in obj) {
        if (Object.prototype.hasOwnProperty.call(obj, key)) {
            result[key] = fn(obj[key], key, obj);
        }
    }

    return result;
});

import curryN from '../function/curryN';

/**
 * Create a new object with the own properties of the first object merged with
 * the own properties of the others objects. If a key exists in several objects,
 * the value from the last object will be used.
 *
 * @param {...Object} sources
 * @return {Object}
 * @example
 *
 * merge({ 'name': 'fred', 'age': 10 }, { 'age': 40 }); //=> { 'name': 'fred', 'age': 40 }
 */
export default curryN(2, (...sources) => Object.assign({}, ...sources));

import curryN from '../function/curryN';
import isPlainObject from '../is/plainObject';
import isArray from '../is/array';
import objectKeys from './keys';

/**
 * Create a new object with the own properties of the first object deeply merged with
 * the own properties of the other objects. If a key exists in several objects, then all values associated with this key
 * are merged recursively unless at least one of them is not a plain object. Merging is done from left to right.
 *
 * @param {...Object} sources
 * @return {Object}
 * @example
 *
 * mergeDeep({ 'name': 'fred', 'info': { 'age': 10, 'sex': 'm' } }, { 'info': { 'age': 40 }); //=> { 'name': 'fred', 'info': { 'age': 40, 'sex': 'm' } }
 */
const mergeDeep = (...sources) => {
    const result = Object.assign(isArray(sources[0]) ? [] : {}, sources[0] || {});

    for (let i = 1; i < sources.length; i++) {
        const src = sources[i];

        if (!src) {
            continue;
        }

        const keys = objectKeys(src);

        for (let j = 0; j < keys.length; j++) {
            const key = keys[j];
            const value = src[key];
            const res = result[key];

            if (isPlainObject(res) && isPlainObject(value)) {
                result[key] = mergeDeep(res, value);
            } else {
                result[key] = value;
            }
        }
    }

    return result;
};

export default curryN(2, mergeDeep);

import curryN from '../function/curryN';
import objectKeys from './keys';

/**
 * Create a new object with the own properties of the first object merged with
 * the own properties of the others objects. If a key exists in several objects
 * provided `fn` will be called and should return resolve value
 *
 * @param {Function} fn function to call if prop conflict apperar. Receives three argument, `valueLeft`, `valueRight`,
 * `key`, `leftObj`, `rightObj`.
 * @param {...Object} sources
 * @return {Object}
 * @example
 *
 * mergeWith((x, y) => x + y, { 'name': 'fred', 'age': 10 }, { 'age': 40 }); //=> { 'name': 'fred', 'age': 50 }
 */
export default curryN(3, (fn, ...sources) => {
    const result = Object.assign({}, sources[0]);

    for (let i = 1; i < sources.length; i++) {
        const source = sources[i];
        const keys = objectKeys(source);

        for (let j = 0; j < keys.length; j++) {
            const key = keys[j];

            if (Object.prototype.hasOwnProperty.call(result, key)) {
                result[key] = fn(result[key], source[key], key, result, source);
            } else {
                result[key] = source[key];
            }
        }
    }

    return result;
});

import curryN from '../function/curryN';

/**
 * Creates an object containing a single key:value pair.
 *
 * @param {String} key
 * @param {*} value
 * @return {Object}
 * @example
 *
 *      objOf('key', 5) // => { key: 5 }
 */
export default curryN(2, (key, value) => ({ [key]: value }));

import curryN from '../function/curryN';
import objectKeys from './keys';

/**
 * Returns a partial copy of an object omitting the keys specified.
 *
 * @param {Array} props an array of String property names to omit from the new object
 * @param {Object} obj The object to copy from
 * @return {Object} A new object with properties from `names` not on it.
 * @example
 *
 *      omit(['a', 'd'], {a: 1, b: 2, c: 3, d: 4}); //=> {b: 2, c: 3}
 */
export default curryN(2, (props = [], obj = {}) => {
    const result = {};
    const keys = objectKeys(obj);

    for (let i = 0; i < keys.length; i++) {
        const prop = keys[i];

        if (props.indexOf(prop) === -1) {
            result[prop] = obj[prop];
        }
    }

    return result;
});

import curryN from '../function/curryN';

/**
 * Retrieve the value at a given path.
 *
 * @param {[String]} paths The path to use.
 * @param {Object} obj The object to retrieve the nested property from.
 * @return {*} The data at `path`.
 * @example
 *
 *      path(['a', 'b'], {a: {b: 2}}); //=> 2
 *      path(['a', 'b'], {c: {b: 2}}); //=> undefined
 */
export default curryN(2, (paths = [], obj = {}) => {
    let val = obj;

    for (let i = 0; i < paths.length; i++) {
        if (val == null) {
            return;
        }

        val = val[paths[i]];
    }

    return val;
});

import curryN from '../function/curryN';
import path from './path';

/**
 * Returns the result of `fn` with given value at `path`.
 *
 * @param {[String]} paths The path to use.
 * @param {Function} fn function to apply
 * @param {Object} obj The object to retrieve the nested property from.
 * @return {*} result of calling fn with nested property.
 *
 * @example
 *
 *      pathApply(['a', 'b'], x => 'is ' + x, {a: {b: 2}}); //=> is 2
 *      pathApply(['a', 'b'], x => x > 0, {a: {b: 2}}); //=> true
 */
export default curryN(3, (paths = [], fn, obj = {}) =>
    fn(path(paths, obj))
);

import curryN from '../function/curryN';
import path from './path';

/**
 * Determines whether a nested path on an object has a specific value
 *
 * @param {Array} paths The path of the nested property to use
 * @param {*} value The value to compare the nested property with
 * @param {Object} obj The object to check the nested property in
 * @return {Boolean} `true` if the value is the nested object property,
 *         `false` otherwise.
 * @example
 *
 *      var user1 = { address: { zipCode: 90210 } };
 *      var user2 = { address: { zipCode: 55555 } };
 *      var user3 = { name: 'Bob' };
 *      var users = [ user1, user2, user3 ];
 *      var isFamous = pathEq(['address', 'zipCode'], 90210);
 *      filter(isFamous, users); //=> [ user1 ]
 */
export default curryN(3, (paths = [], value, obj = {}) => path(paths, obj) === value);

