import curryN from '../function/curryN';
import path from './path';

/**
 * If the given, non-null object has a value at the given path, returns the
 * value at that path. Otherwise returns the provided default value.
 *
 * @param {[String]} paths The path to use.
 * @param {*} value The default value.
 * @param {Object} obj The object to retrieve the nested property from.
 * @return {*} The data at `path` of the supplied object or the default value.
 * @example
 *
 *      pathOr(['a', 'b'],'N/A', {a: {b: 2}}); //=> 2
 *      pathOr(['a', 'b'], 'N/A', {c: {b: 2}}); //=> "N/A"
 */
export default curryN(3, (paths = [], value, obj = {}) => {
    const v = path(paths, obj);

    return v != null ? v : value;
});

import curryN from '../function/curryN';
import isObject from '../is/object';

/**
 * Returns the result of "setting" the portion of the given data structure
 * focused by the given paths to the given value.
 *
 * @param {[String]} paths
 * @param {*} value
 * @param {Object} obj
 * @return {Object}
 * @example
 *
 *      pathSet(['a', 'b'], 3, {}) // => { a: { b: 3 } }
 */
export default curryN(3, (paths = [], value, obj = {}) => {
    const n = paths.length - 1;
    const result = Object.assign({}, obj);
    let val = result;
    let v;

    for (let i = 0; i < n; i++) {
        v = val[paths[i]];
        if (isObject(v)) {
            v = Object.assign({}, v);
        } else {
            v = {};
        }

        val[paths[i]] = v;
        val = v;
    }
    val[paths[n]] = value;

    return result;
});

import curryN from '../function/curryN';

/**
 * Returns a partial copy of an object containing only the keys specified. If
 * the key does not exist, the property is ignored.
 *
 * @param {[String]} props an array of String property names to copy onto a new object
 * @param {Object} obj The object to copy from
 * @return {Object} A new object with only properties from `names` on it.
 * @example
 *
 *      pick(['a', 'd'], {a: 1, b: 2, c: 3, d: 4}); //=> {a: 1, d: 4}
 *      pick(['a', 'e', 'f'], {a: 1, b: 2, c: 3, d: 4}); //=> {a: 1}
 */
export default curryN(2, (props = [], obj = {}) => {
    const result = {};

    for (let i = 0; i < props.length; i++) {
        const prop = props[i];

        if (prop in obj) {
            result[prop] = obj[prop];
        }
    }

    return result;
});

import curryN from '../function/curryN';

/**
 * Returns a partial copy of an object containing only the keys with `fn` predicate returns true
 *
 * @param {Function} fn predicate to pick keys. Receives three argument, `value`, `key`, `obj`.
 * @param {Object} obj The object to copy from
 * @return {Object} A new object with only properties from `names` on it.
 * @example
 *
 *      pickBy(x => x > 2, {a: 1, b: 2, c: 3, d: 4}); //=> {c: 3, d: 4}
 */
export default curryN(2, (fn, obj = {}) => {
    const result = {};

    for (const prop in obj) {
        if (fn(obj[prop], prop, obj)) {
            result[prop] = obj[prop];
        }
    }

    return result;
});

import curryN from '../function/curryN';
import prop from './prop';

/**
 * Returns the result of `fn` with value of property in `obj`.
 *
 * @param {String} propName The property name to apply.
 * @param {Function} fn function to apply
 * @param {Object} obj The object to retrieve the property from.
 * @return {*} result of calling fn with property.
 *
 * @example
 *
 *      propApply('a', x => 'is ' + x, {a: 2}); //=> is 2
 *      propApply('b', x => x > 0, {b: 2}); //=> true
 */
export default curryN(3, (propName, fn, obj) =>
    fn(prop(propName, obj))
);

import curryN from '../function/curryN';
import prop from './prop';

/**
