import promiseTap from '../tap';

describe('promiseTap', () => {
    it('should run promise and return passed argument', () => {
        const fn = jest.fn(x => 0);
        const tapped = promiseTap(fn);
        const saveResult = jest.fn();

        return Promise.resolve(5)
            .then(tapped)
            .then(saveResult)
            .then(() => {
                expect(fn).toBeCalledWith(5);
                expect(saveResult).toBeCalledWith(5);
            });
    });
});

import curryN from '../function/curryN';

/**
 * Returns a promise that rejects with a value returned
 * by the supplied function when passed the supplied payload
 *
 * @param {Function} func
 * @param {*[]} payload
 * @returns {Promise}
 * @example
 *      rejectWith(toLowerCase, 'Error').catch( err => console.error(err) );// => 'error'
 */
export default curryN(2, (func, ...payload) => {
    return Promise.reject(func(...payload));
});

import curryN from '../function/curryN';

/**
 * Returns a promise that resolves with a value returned
 * by the supplied function when passed the supplied payload
 *
 * @param {Function} func
 * @param {*[]} payload
 * @returns {Promise}
 * @example
 *      rejectWith(toLowerCase, 'OK').then( res => console.info(res) );// => 'ok'
 */
export default curryN(2, (func, ...payload) => {
    return Promise.resolve(func(...payload));
});

import curryN from '../function/curryN';

/**
 * Runs the given promise with the supplied argument. Returns the argument, when promise is resolved.
 *
 * @param {Function} fn The function to call with `x`. The return value of `fn` will be thrown away.
 * @param {*} x
 * @return {*} `x`.
 * @example
 *
 *      var sayX = x => console.log('x is ' + x);
 *      Promise.resolve(5).then(promiseTap(sayX));
 *      // logs 'x is 5'
 */
export default curryN(2, (fn, x) => Promise.resolve().then(() => fn(x)).then(() => x));

examples:
var sayX = x => console.log('x is ' + x);
Promise.resolve(5).then(promiseTap(sayX));
// logs 'x is 5'

