 *
 * @param {string} postfix
 * @param {string} str
 * @return {Boolean}
 * @example
 *
 *      endsWith('c', 'abc')                //=> true
 *      endsWith('b', 'abc')                //=> false
 */
export default curryN(2, (postfix = '', str = '') => str.slice(-postfix.length) === postfix);

const escapeRegExp = /[&<>"']/g;

const htmlEscapes = {
    '&': '&amp;',
    '<': '&lt;',
    '>': '&gt;',
    '"': '&quot;',
    "'": '&#39;'
};
const escape = char => htmlEscapes[char];

/**
 * Converts the characters "&", "<", ">", '"', and "'" in `string` to their
 * corresponding HTML entities.
 *
 * @param {string} str The string to escape.
 * @returns {string} Returns the escaped string.
 * @example
 *
 * escape('fred, barney, & pebbles'); // => 'fred, barney, &amp; pebbles'
 */
export default (str = '') =>
    str.replace(escapeRegExp, escape);

import curryN from '../function/curryN';
import isString from '../is/string';
/**
 * Returns a string containing a repeated identical value.
 *
 * @param {Number} n The number to repeat value.
 * @param {String} value The value to repeat.
 * @return {String} A new string containing `n` `value`s.
 * @example
 *
 *      repeat(5, 'h'); //=> 'hhhhh'
 *
 */
export default curryN(2, (n = 0, value) => {
    if (!isString(value)) { return ''; }

    let result = '';

    for (let i = 0; i < n; i++) {
        result += value;
    }

    return result;
});

import curryN from '../function/curryN';

/**
 * Checks if a string starts with the provided prefix
 *
 * @param {string} prefix
 * @param {string} str
 * @return {Boolean}
 * @example
 *
 *      startsWith('a', 'abc')                //=> true
 *      startsWith('b', 'abc')                //=> false
 */
export default curryN(2, (prefix = '', str = '') => str.slice(0, prefix.length) === prefix);

import curryN from '../function/curryN';

/**
 * Replaces string keywords with provided values
 *
 * @param {Object} replacements object with keywords and corresponding replacements
 * @param {String} str string with keywords in a correct format '%{keyword}'
 * @return {String} String with replaced keywords
 * @example
 *
 *      template({ fruit: 'banana' }, 'Petya has a %{fruit}') // => Petya has a banana
 */
export default curryN(2, (replacements = {}, str = '') => str.replace(/%\{.+?\}/g, match => {
    const replacementKey = match.replace(/\W/g, '');

    return replacements[replacementKey];
}));

import curryN from '../function/curryN';

/**
 * Determines whether a given string matches a given regular expression.
 *
 * @param {RegExp} pattern
 * @param {String} str
 * @return {Boolean}
 */
export default curryN(2, (pattern = /()/, str = '') => pattern.test(str));

/**
 * Converts `value` to a string. An empty string is returned for `null`
 * and `undefined` values.
 *
 * @param {*} val The value to convert.
 * @returns {string} Returns the converted string.
 * @example
 *
 * toString(null); // => ''
 *
 * toString('test'); // => 'test'
 *
 * toString([1, 2, 3]); => '1,2,3'
 */
export default val => {
    if (val == null) {
        return '';
    }

    return `${val}`;
};

const unescapeRegExp = /&(?:amp|lt|gt|quot|#39);/g;

const htmlUnescapes = {
    '&amp;': '&',
    '&lt;': '<',
    '&gt;': '>',
    '&quot;': '"',
    '&#39;': "'"
};
const unescape = char => htmlUnescapes[char];

/**
 * The inverse of `escape`; this method converts the HTML entities
 * `&amp;`, `&lt;`, `&gt;`, `&quot;`, and `&#39;` in `string` to
 * their corresponding characters.
 *
 * @param {string} str The string to unescape.
 * @returns {string} Returns the unescaped string.
 * @example
 *
 * unescape('fred, barney, &amp; pebbles'); // => 'fred, barney, & pebbles'
 */
export default (str = '') =>
    str.replace(unescapeRegExp, unescape);

/**
 * Gives a single-word string description of the (native) type of a value,
 * returning such answers as 'Object', 'Number', 'Array', or 'Null'. Does not
 * attempt to distinguish user Object types any further, reporting them all as
 * 'Object'.
 *
 * @param {*} x The value to test
 * @return {String}
 * @example
 *
 *      type({}); //=> "Object"
 *      type(1); //=> "Number"
 *      type(false); //=> "Boolean"
 *      type('s'); //=> "String"
 *      type(null); //=> "Null"
 *      type([]); //=> "Array"
 *      type(/[A-z]/); //=> "RegExp"
 */
export default x => {
    if (x === null) {
        return 'Null';
    }

    if (x === undefined) {
        return 'Undefined';
    }

    return Object.prototype.toString.call(x).slice(8, -1);
};

